clear;clc;M = 20;Time = 1;dt = 0.01; dx = 0.1;D = 0.1;T = ones(Time/dt+1,M).*25;dT = zeros(Time/dt+1,M);for t=1:100;  for i=1:M;        c = T(t,i);        if (i==1);      l = 100;    else;      l = T(t,i-1);    endif;        if (i==M);      r = 25;    else;      r = T(t,i+1);    endif;       dT(t,i) = D * (l-2*c+r)/(dx*dx);    endfor;  T(t+1,:) = T(t,:).+ dT(t,:).*dt;  
endfor;figure(1);hold on;plot(T(10,:),'r-');plot(T(20,:),'r-');plot(T(30,:),'r-');plot(T(40,:),'g-');plot(T(50,:),'g-');plot(T(60,:),'g-');plot(T(70,:),'b-');plot(T(80,:),'b-');plot(T(90,:),'b-');plot(T(100,:),'b--');legend('0.1s','0.2s','0.3s','0.4s','0.5s','0.6s','0.7s','0.8s','0.9s','1s');