clear;clc;caxis ([0 100])M = 30;Time = 1;dt = 0.01; dx = 0.1;D = 0.1;C = ones(Time/dt+1,M,M).*25;dT = zeros(Time/dt+1,M,M);for t=1:100;  for i=1:M;    for j=1:M;      c = C(t,i,j);            if (i==1);        l = 100;      else;        l = C(t,i-1,j);      endif;            if (i==M);        r = 25;      else;        r = C(t,i+1,j);      endif;          if (j==1);        d = 100;      else;        d = C(t,i,j-1);      endif;            if (j==M);        u = 25;      else;        u = C(t,i,j+1);      endif;                        dT(t,i,j) = D * (l+r+u+d-4*c)/(dx*dx);      endfor;  endfor;  C(t+1,:,:) = C(t,:,:).+ dT(t,:,:).*dt;      CI = reshape(C(t,:,:),[M M]);  image(CI);  pause(dt);endfor;