clear;clc;function [dfx dfy] = hao_ham_bac_2(f,dx,dy)  [X Y] = size(f);  dfx = zeros(X,Y);  dfy = zeros(X,Y);    for i=1:X    for j=1:Y      c = f(i,j);      if (i==1);        u = 100;      else;        u = f(i-1,j);      endif;            if (i==X);        d = 100;      else;        d = f(i+1,j);      endif;          if (j==1);        l = 100;      else;        l = f(i,j-1);      endif;            if (j==Y);        r = 100;      else;        r = f(i,j+1);      endif;            dfx(i,j) = (u+d-2*c)/(dx*dx);      dfy(i,j) = (l+r-2*c)/(dy*dy);          endfor;  endfor;   endfunction;  caxis ([0 100])M = 30;T = 100;dt = 1; dx = 1;dy = 1;n = M/dx;m = M/dy;D = 0.1;C = ones(m,n).*25; FD = zeros(size(C));for t=1:dt:T;    [d2Cx d2Cy] = hao_ham_bac_2(C,dx,dy);  FD = d2Cx + d2Cy;    C = C + D*dt.*FD;  figure(1), clf  title(['t = ' num2str(t)]);  image(C);    figure(2), clf  title(['t = ' num2str(t)]);  mesh(C);    pause(dt/10);endfor;